<template>
  <div class="app-container">
    <header>
      <h1>AIÁü•ËØÜÁ´ûÁ≠î</h1>
      <button @click="toggleAudio" class="audio-toggle">
        {{ audioEnabled ? 'üîä' : 'üîá' }}
      </button>
      <button @click="testAudio" class="test-audio-btn">ÊµãËØïÈü≥È¢ë</button>
    </header>
    
    <main>
      <div v-if="$store.state.error" class="error-message">
        {{ $store.state.error }}
        <button @click="retryFetchQuestions">ÈáçËØï</button>
      </div>
      
      <div v-else-if="$store.state.isLoading" class="loading">
        <div class="spinner"></div>
        <p>Ê≠£Âú®Âä†ËΩΩÈ¢òÁõÆ...</p>
      </div>
      
      <div v-else-if="!$store.state.quizStarted" class="welcome-screen">
        <h2>Ê¨¢ËøéÂèÇÂä†AIÁü•ËØÜÁ´ûÁ≠î!</h2>
        <p>ÊµãËØï‰Ω†ÂØπ‰∫∫Â∑•Êô∫ËÉΩÁöÑ‰∫ÜËß£Á®ãÂ∫¶ÔºåÂÖ±{{ $store.state.questions.length }}ÈÅìÈ¢ò„ÄÇ</p>
        <p class="note">Ê≥®ÊÑèÔºöËØ≠Èü≥ÊúóËØªÂäüËÉΩÂèØËÉΩÊöÇÊó∂‰∏çÂèØÁî®Ôºå‰ΩÜ‰∏çÂΩ±ÂìçÁ≠îÈ¢ò„ÄÇ</p>
        <button @click="startQuiz" class="start-button">ÂºÄÂßãÁ≠îÈ¢ò</button>
      </div>
      
      <div v-else-if="$store.state.quizFinished" class="results-screen">
        <h2>ÊµãÈ™åÂÆåÊàê!</h2>
        <div class="score-container">
          <div class="score-circle">
            <span class="score-text">{{ $store.getters.score.percentage }}%</span>
          </div>
          <p>ÊÇ®Á≠îÂØπ‰∫Ü{{ $store.getters.score.correct }}/{{ $store.getters.score.total }}ÈÅìÈ¢ò</p>
        </div>
        
        <div class="answers-review">
          <h3>Á≠îÈ¢òËØ¶ÊÉÖ</h3>
          <div v-for="(question, index) in $store.state.questions" :key="question.id" class="question-review">
            <div class="question-header">
              <span class="question-number">ÈóÆÈ¢ò {{ index + 1 }}</span>
              <span :class="['result-badge', isCorrect(index) ? 'correct' : 'incorrect']">
                {{ isCorrect(index) ? '‚úì Ê≠£Á°Æ' : '‚úó ÈîôËØØ' }}
              </span>
            </div>
            <p class="question-text">{{ question.question }}</p>
            <div class="answer-details">
              <p v-if="question.type === 'choice'">
                <strong>‰Ω†ÁöÑÁ≠îÊ°à:</strong> {{ $store.state.userAnswers[index] || 'Êú™Á≠î' }}<br>
                <strong>Ê≠£Á°ÆÁ≠îÊ°à:</strong> {{ question.correctAnswer }}
              </p>
              <p v-else-if="question.type === 'judgment'">
                <strong>‰Ω†ÁöÑÁ≠îÊ°à:</strong> {{ $store.state.userAnswers[index] === true ? 'Ê≠£Á°Æ' : 'ÈîôËØØ' }}<br>
                <strong>Ê≠£Á°ÆÁ≠îÊ°à:</strong> {{ question.correctAnswer === true ? 'Ê≠£Á°Æ' : 'ÈîôËØØ' }}
              </p>
              <p v-else>
                <strong>‰Ω†ÁöÑÁ≠îÊ°à:</strong> {{ $store.state.userAnswers[index] || 'Êú™Á≠î' }}<br>
                <strong>Ê≠£Á°ÆÁ≠îÊ°à:</strong> {{ question.correctAnswer }}
              </p>
              <p class="explanation">{{ question.explanation }}</p>
            </div>
          </div>
        </div>
        
        <button @click="restartQuiz" class="restart-button">ÈáçÊñ∞ÂºÄÂßã</button>
      </div>
      
      <div v-else class="question-container">
        <div class="progress-bar">
          <div class="progress-fill" :style="{ width: $store.getters.progress + '%' }"></div>
        </div>
        <p class="question-counter">ÈóÆÈ¢ò {{ $store.state.currentQuestionIndex + 1 }}/{{ $store.state.questions.length }}</p>
        
        <div v-if="$store.getters.currentQuestion" class="current-question">
          <h3>{{ $store.getters.currentQuestion.question }}</h3>
          
          <!-- Âà§Êñ≠È¢ò -->
          <div v-if="$store.getters.currentQuestion.type === 'judgment'" class="judgment-question">
            <button 
              @click="submitAnswer(true)" 
              :class="['judgment-btn', $store.state.userAnswers[$store.state.currentQuestionIndex] === true ? 'selected' : '']"
              :disabled="$store.state.feedbackShown"
            >
              Ê≠£Á°Æ
            </button>
            <button 
              @click="submitAnswer(false)" 
              :class="['judgment-btn', $store.state.userAnswers[$store.state.currentQuestionIndex] === false ? 'selected' : '']"
              :disabled="$store.state.feedbackShown"
            >
              ÈîôËØØ
            </button>
          </div>
          
          <!-- ÈÄâÊã©È¢ò -->
          <div v-else-if="$store.getters.currentQuestion.type === 'choice'" class="choice-question">
            <div 
              v-for="option in $store.getters.currentQuestion.options" 
              :key="option"
              @click="submitAnswer(option)"
              :class="['option', $store.state.userAnswers[$store.state.currentQuestionIndex] === option ? 'selected' : '']"
              :disabled="$store.state.feedbackShown"
            >
              {{ option }}
            </div>
          </div>
          
          <!-- ÁÆÄÁ≠îÈ¢ò -->
          <div v-else-if="$store.getters.currentQuestion.type === 'simple_answer'" class="simple-answer-question">
            <input 
              type="text" 
              v-model="answerInput" 
              placeholder="ËØ∑ËæìÂÖ•‰Ω†ÁöÑÁ≠îÊ°à"
              :disabled="$store.state.feedbackShown"
            />
            <button @click="submitAnswer(answerInput)" :disabled="$store.state.feedbackShown || !answerInput">
              Êèê‰∫§
            </button>
          </div>
          
          <!-- Á≠îÊ°àÂèçÈ¶à -->
          <div v-if="$store.state.feedbackShown" class="feedback">
            <div class="feedback-header" :class="isCurrentAnswerCorrect ? 'correct' : 'incorrect'">
              {{ isCurrentAnswerCorrect ? '‚úì Á≠îÂØπ‰∫Ü!' : '‚úó Á≠îÈîô‰∫Ü!' }}
            </div>
            <p class="feedback-text">
              {{ $store.getters.currentQuestion.explanation }}
            </p>
            <button @click="nextQuestion" class="next-button">
              {{ isLastQuestion ? 'Êü•ÁúãÁªìÊûú' : '‰∏ã‰∏ÄÈ¢ò' }}
            </button>
          </div>
        </div>
      </div>
    </main>
    
    <footer>
      <p>¬© 2050 AIÁü•ËØÜÁ´ûÁ≠î | ÁâàÊùÉÊâÄÊúâ</p>
    </footer>
  </div>

  <!-- Ê∑ªÂä†ÈöêËóèÁöÑÈü≥È¢ëÊí≠ÊîæÂô®ÁªÑ‰ª∂ -->
  <audio ref="questionAudio" style="display:none"></audio>
  <audio ref="feedbackAudio" style="display:none"></audio>
  <audio ref="testAudio" style="display:none"></audio>
</template>

<script>
export default {
  name: 'App',
  data() {
    return {
      answerInput: '',
      testAudioData: 'data:audio/mp3;base64,'
    }
  },
  computed: {
    audioEnabled() {
      return this.$store.state.audioEnabled;
    },
    isCurrentAnswerCorrect() {
      const currentQuestion = this.$store.getters.currentQuestion;
      if (!currentQuestion) return false;
      
      const questionId = currentQuestion.id;
      const feedback = this.$store.getters.getFeedback(questionId);
      
      return feedback && feedback.isCorrect;
    },
    isLastQuestion() {
      return this.$store.getters.isLastQuestion;
    }
  },
  methods: {
    startQuiz() {
      // ÈáçÁΩÆÂπ∂Ëé∑ÂèñÈ¢òÁõÆ
      this.$store.commit('RESET_QUIZ');
      this.$store.dispatch('fetchQuestions').then(() => {
        // Ëé∑ÂèñÈ¢òÁõÆÊàêÂäüÂêéÂêØÂä®ÊµãÈ™å
        this.$store.commit('START_QUIZ');
        console.log('ÊµãÈ™åÂ∑≤ÂêØÂä®ÔºåquizStarted =', this.$store.state.quizStarted);
      });
    },
    
    restartQuiz() {
      // ÈáçÁΩÆÂπ∂Ëé∑ÂèñÈ¢òÁõÆ
      this.$store.commit('RESET_QUIZ');
      this.$store.dispatch('fetchQuestions').then(() => {
        // Ëé∑ÂèñÈ¢òÁõÆÊàêÂäüÂêéÂêØÂä®ÊµãÈ™å
        this.$store.commit('START_QUIZ');
      });
    },
    
    submitAnswer(answer) {
      if (this.$store.state.feedbackShown) return;
      
      // ‰ΩøÁî®store‰∏≠ÁöÑÊñπÊ≥ï
      const currentQuestion = this.$store.getters.currentQuestion;
      if (currentQuestion) {
        try {
          // ËÆæÁΩÆÁî®Êà∑Á≠îÊ°à
          this.$store.commit('SET_USER_ANSWER', { 
            questionId: currentQuestion.id, 
            answer: answer 
          });
          
          // ÊòæÁ§∫ÂèçÈ¶à
          this.$store.commit('SET_FEEDBACK_SHOWN', true);
          
          // Ëé∑ÂèñÁ≠îÊ°àÂèçÈ¶à
          this.$store.dispatch('submitAnswer', {
            questionId: currentQuestion.id,
            answer: answer,
            questionType: currentQuestion.type,
            question: currentQuestion.question,
            correctAnswer: currentQuestion.correctAnswer
          });
          
          // Âª∂Ëøü‰∏ÄÊÆµÊó∂Èó¥ÂêéÂ∞ùËØïÊí≠ÊîæÂèçÈ¶àÈü≥È¢ë
          setTimeout(() => {
            this.playFeedbackAudio(currentQuestion.id);
          }, 1000); // Âª∂Ëøü1Áßí
        } catch (error) {
          console.error('Êèê‰∫§Á≠îÊ°àÊó∂Âá∫Èîô:', error);
        }
      }
    },
    
    // Êí≠ÊîæÈóÆÈ¢òÈü≥È¢ë
    playQuestionAudio() {
      const currentQuestion = this.$store.getters.currentQuestion;
      if (currentQuestion && this.$store.state.audioEnabled) {
        console.log('Â∞ùËØïÊí≠ÊîæÈóÆÈ¢òÈü≥È¢ë:', currentQuestion.id);
        
        if (!currentQuestion.audio) {
          console.warn('ÈóÆÈ¢òÊ≤°ÊúâÈü≥È¢ëÊï∞ÊçÆ');
          return;
        }
        
        // Â¶ÇÊûúTTSÊúçÂä°ÊúâÈîôËØØÔºåÊòæÁ§∫ÈîôËØØÊ∂àÊÅØ‰ΩÜ‰∏çÂΩ±ÂìçÂ∫îÁî®ÁªßÁª≠‰ΩøÁî®
        if (currentQuestion.audio.error) {
          console.warn('TTSÊúçÂä°ÈîôËØØ:', currentQuestion.audio.error);
          return;
        }
        
        // Â¶ÇÊûúÈü≥È¢ëÊï∞ÊçÆ‰∏∫Á©∫ÔºåË∑≥ËøáÊí≠Êîæ
        if (!currentQuestion.audio.audio_base64 && !currentQuestion.audio.file_path) {
          console.warn('Èü≥È¢ëÊï∞ÊçÆ‰∏∫Á©∫');
          return;
        }
        
        const audioElement = this.$refs.questionAudio;
        if (audioElement) {
          try {
            if (currentQuestion.audio.audio_base64) {
              console.log('‰ΩøÁî®base64Èü≥È¢ëÊï∞ÊçÆ');
              audioElement.src = `data:audio/mp3;base64,${currentQuestion.audio.audio_base64}`;
            } else if (currentQuestion.audio.file_path) {
              console.log('‰ΩøÁî®Èü≥È¢ëÊñá‰ª∂Ë∑ØÂæÑ:', currentQuestion.audio.file_path);
              // Á°Æ‰øùË∑ØÂæÑ‰ª•Ê≠£Á°ÆÁöÑÂΩ¢Âºè‰ΩøÁî®
              let audioPath = currentQuestion.audio.file_path;
              if (!audioPath.startsWith('http') && !audioPath.startsWith('/api')) {
                audioPath = `/api${audioPath}`;
              }
              audioElement.src = audioPath;
            } else {
              console.warn('ÈóÆÈ¢òÈü≥È¢ëÊï∞ÊçÆÊ†ºÂºè‰∏çÊ≠£Á°Æ');
              return;
            }
            
            // ÁõëÂê¨Âä†ËΩΩ‰∫ã‰ª∂
            audioElement.onloadeddata = () => {
              console.log('Èü≥È¢ëÊï∞ÊçÆÂ∑≤Âä†ËΩΩÔºåÂáÜÂ§áÊí≠Êîæ');
            };
            
            // ÁõëÂê¨ÈîôËØØ‰∫ã‰ª∂
            audioElement.onerror = (e) => {
              console.error('Èü≥È¢ëÂä†ËΩΩÂ§±Ë¥•:', e);
            };
            
            // ÁõëÂê¨Êí≠Êîæ‰∫ã‰ª∂
            audioElement.onplay = () => {
              console.log('Èü≥È¢ëÂºÄÂßãÊí≠Êîæ');
            };
            
            // Â∞ùËØïÊí≠Êîæ
            const playPromise = audioElement.play();
            if (playPromise !== undefined) {
              playPromise
                .then(() => {
                  console.log('Èü≥È¢ëÊí≠ÊîæÊàêÂäü');
                })
                .catch(e => {
                  console.error('Êó†Ê≥ïÊí≠ÊîæÈóÆÈ¢òÈü≥È¢ë:', e);
                });
            }
          } catch (error) {
            console.error('Êí≠ÊîæÈóÆÈ¢òÈü≥È¢ëÊó∂Âá∫Èîô:', error);
          }
        }
      }
    },
    
    // Êí≠ÊîæÂèçÈ¶àÈü≥È¢ë
    playFeedbackAudio(questionId) {
      console.log('Â∞ùËØïÊí≠ÊîæÂèçÈ¶àÈü≥È¢ë:', questionId);
      const feedback = this.$store.getters.getFeedback(questionId);
      
      if (!feedback) {
        console.warn('Êú™ÊâæÂà∞ÈóÆÈ¢òÁöÑÂèçÈ¶à');
        return;
      }
      
      console.log('ÊâæÂà∞ÁöÑÂèçÈ¶à:', feedback);
      
      if (feedback && feedback.audio && this.$store.state.audioEnabled) {
        // Â¶ÇÊûúTTSÊúçÂä°ÊúâÈîôËØØÔºåÊòæÁ§∫ÈîôËØØÊ∂àÊÅØ‰ΩÜ‰∏çÂΩ±ÂìçÂ∫îÁî®ÁªßÁª≠‰ΩøÁî®
        if (feedback.audio.error) {
          console.warn('TTSÊúçÂä°ÈîôËØØ:', feedback.audio.error);
          return;
        }
        
        // Â¶ÇÊûúÈü≥È¢ëÊï∞ÊçÆ‰∏∫Á©∫ÔºåË∑≥ËøáÊí≠Êîæ
        if (!feedback.audio.audio_base64 && !feedback.audio.file_path) {
          console.warn('Èü≥È¢ëÊï∞ÊçÆ‰∏∫Á©∫');
          return;
        }
        
        const audioElement = this.$refs.feedbackAudio;
        if (audioElement) {
          try {
            if (feedback.audio.audio_base64) {
              console.log('‰ΩøÁî®base64Èü≥È¢ëÊï∞ÊçÆ');
              audioElement.src = `data:audio/mp3;base64,${feedback.audio.audio_base64}`;
            } else if (feedback.audio.file_path) {
              console.log('‰ΩøÁî®Èü≥È¢ëÊñá‰ª∂Ë∑ØÂæÑ:', feedback.audio.file_path);
              // Á°Æ‰øùË∑ØÂæÑ‰ª•Ê≠£Á°ÆÁöÑÂΩ¢Âºè‰ΩøÁî®
              let audioPath = feedback.audio.file_path;
              if (!audioPath.startsWith('http') && !audioPath.startsWith('/api')) {
                audioPath = `/api${audioPath}`;
              }
              audioElement.src = audioPath;
            } else {
              console.warn('ÂèçÈ¶àÈü≥È¢ëÊï∞ÊçÆÊ†ºÂºè‰∏çÊ≠£Á°Æ');
              return;
            }
            
            // ÁõëÂê¨Âä†ËΩΩ‰∫ã‰ª∂
            audioElement.onloadeddata = () => {
              console.log('ÂèçÈ¶àÈü≥È¢ëÊï∞ÊçÆÂ∑≤Âä†ËΩΩÔºåÂáÜÂ§áÊí≠Êîæ');
            };
            
            // ÁõëÂê¨ÈîôËØØ‰∫ã‰ª∂
            audioElement.onerror = (e) => {
              console.error('ÂèçÈ¶àÈü≥È¢ëÂä†ËΩΩÂ§±Ë¥•:', e);
            };
            
            // ÁõëÂê¨Êí≠Êîæ‰∫ã‰ª∂
            audioElement.onplay = () => {
              console.log('ÂèçÈ¶àÈü≥È¢ëÂºÄÂßãÊí≠Êîæ');
            };
            
            // Â∞ùËØïÊí≠Êîæ
            const playPromise = audioElement.play();
            if (playPromise !== undefined) {
              playPromise
                .then(() => {
                  console.log('ÂèçÈ¶àÈü≥È¢ëÊí≠ÊîæÊàêÂäü');
                })
                .catch(e => {
                  console.error('Êó†Ê≥ïÊí≠ÊîæÂèçÈ¶àÈü≥È¢ë:', e);
                });
            }
          } catch (error) {
            console.error('Êí≠ÊîæÂèçÈ¶àÈü≥È¢ëÊó∂Âá∫Èîô:', error);
          }
        }
      } else {
        console.warn('ÂèçÈ¶àÊ≤°ÊúâÈü≥È¢ëÊï∞ÊçÆÊàñÈü≥È¢ëÂ∑≤Á¶ÅÁî®');
      }
    },
    
    nextQuestion() {
      // ÈöêËóèÂΩìÂâçÂèçÈ¶à
      this.$store.commit('SET_FEEDBACK_SHOWN', false);
      this.answerInput = '';
      
      if (this.isLastQuestion) {
        this.$store.commit('SET_QUIZ_COMPLETED', true);
        this.$store.dispatch('getFinalScore');
      } else {
        this.$store.commit('NEXT_QUESTION');
        // Âú®ÂàáÊç¢Âà∞‰∏ã‰∏ÄÈ¢òÂêéÊí≠ÊîæÈóÆÈ¢òÈü≥È¢ë
        this.$nextTick(() => {
          this.playQuestionAudio();
        });
      }
    },
    retryFetchQuestions() {
      this.$store.dispatch('fetchQuestions')
    },
    isCorrect(index) {
      const question = this.$store.state.questions[index]
      const userAnswer = this.$store.state.userAnswers[index]
      
      if (!question || userAnswer === undefined) return false
      
      if (question.type === 'simple_answer') {
        return userAnswer.trim().toLowerCase() === question.correctAnswer.trim().toLowerCase()
      }
      
      return userAnswer === question.correctAnswer
    },
    
    toggleAudio() {
      this.$store.commit('toggleAudio');
    },
    
    testAudio() {
      console.log('ÊµãËØïÈü≥È¢ëÊí≠Êîæ...');
      const audioElement = this.$refs.testAudio;
      if (audioElement) {
        audioElement.src = this.testAudioData;
        audioElement.onloadeddata = () => {
          console.log('ÊµãËØïÈü≥È¢ëÂ∑≤Âä†ËΩΩ');
        };
        audioElement.onplay = () => {
          console.log('ÊµãËØïÈü≥È¢ëÂºÄÂßãÊí≠Êîæ');
        };
        audioElement.onerror = (e) => {
          console.error('ÊµãËØïÈü≥È¢ëÂä†ËΩΩÂ§±Ë¥•:', e);
        };
        
        const playPromise = audioElement.play();
        if (playPromise !== undefined) {
          playPromise
            .then(() => {
              console.log('ÊµãËØïÈü≥È¢ëÊí≠ÊîæÊàêÂäü');
              alert('Â¶ÇÊûúÊÇ®ËÉΩÂê¨Âà∞Â£∞Èü≥ÔºåËØ¥ÊòéÈü≥È¢ëÂäüËÉΩÊ≠£Â∏∏ÔºÅ');
            })
            .catch(e => {
              console.error('Êó†Ê≥ïÊí≠ÊîæÊµãËØïÈü≥È¢ë:', e);
              alert('Êó†Ê≥ïÊí≠ÊîæÈü≥È¢ë: ' + e.message);
            });
        }
      }
    }
  },
  watch: {
    // ÁõëÂê¨ÂΩìÂâçÈóÆÈ¢òÁ¥¢ÂºïÂèòÂåñÔºåÊí≠ÊîæÈóÆÈ¢òÈü≥È¢ë
    '$store.state.currentQuestionIndex': function() {
      this.$nextTick(() => {
        this.playQuestionAudio();
      });
    }
  },
  mounted() {
    // ÂàùÂßãÂåñÊó∂Êí≠ÊîæÁ¨¨‰∏Ä‰∏™ÈóÆÈ¢òÁöÑÈü≥È¢ë
    this.$nextTick(() => {
      if (this.$store.state.quizStarted && this.$store.getters.currentQuestion) {
        this.playQuestionAudio();
      }
    });
  },
  created() {
    this.$store.dispatch('fetchQuestions')
  }
}
</script>

